    //         if (response.ok) {
    //             setTodos(json);
    //         }
    //     }

    //     fetchTodos();

    // }, [])
    return ( 
        <TodosContext.Provider value={{...state, dispatch}}>
              {props.children}
        </TodosContext.Provider>
     );
}
 
export default TodosContextProvider;
